<script>
import { animateJS } from "~/plugins/animations";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
class FrameAnimation {
  constructor(canvas, parent, trigger, pined) {
    this.canvas = canvas;
    this.ctx = this.canvas.getContext("2d");
    this.parent = parent;
    this.trigger = trigger;
    this.pined = pined;
    this.frameCount = 301;
    this.allImagesLoaded = false;

    this.resizeHandler = this.resizeHandler.bind(this);
    this.init();
  }
  async init() {
    this.setSize();
    this.setCanvasSize();
    await this.loadFirstImage();
    if (window.innerWidth < 768) return;
    this.setupScrollTrigger();
    await this.preloadImages();

    // this.resizeHandler();
    this.allImagesLoaded = true;
    // window.addEventListener("resize", this.resizeHandler);
  }
  setCanvasSize() {
    this.canvas.width = this.size.width;
    this.canvas.height = this.size.height;
  }
  setSize() {
    const box = this.parent.getBoundingClientRect();
    this.size = {
      width: box.width,
      height: box.height,
    };
  }

  startExpression() {
    const vw = window.innerWidth;
    const box = this.parent.getBoundingClientRect();
    this.offset = vw >= 1440 ? 32 : vw >= 1024 ? 24 : 16;
    if (window.innerWidth <= 768) {
      return `${-(window.innerHeight - box.height) / 2 + 40}px top`;
    }
    return `bottom ${window.innerHeight - this.offset}px`;
  }

  currentFrame(frame) {
    const offsetFrame = 86;
    const index = frame + offsetFrame;

    return `/images/about/frames/video_${index
      .toString()
      .padStart(6, "0")}.jpg`;
  }

  preloadImages() {
    const promises = [];
    for (let i = 0; i < this.frameCount; i++) {
      const img = new Image();
      promises.push(
        new Promise((resolve, reject) => {
          img.onload = resolve;
          img.src = this.currentFrame(i);
        })
      );
    }
    return Promise.all([...promises]);
  }

  updateImage(index) {
    this.img.src = this.currentFrame(index);
    this.ctx.drawImage(this.img, 0, 0, this.size.width, this.size.height);
  }

  loadFirstImage() {
    return new Promise((resolve, reject) => {
      this.img = new Image();
      this.img.src = this.currentFrame(0);
      this.img.onload = () => {
        this.ctx.drawImage(this.img, 0, 0, this.size.width, this.size.height);
        resolve();
      };
    });
  }

  setupScrollTrigger() {
    this.scrollTrigger = ScrollTrigger.create({
      trigger: this.trigger,
      pin: true,
      pinSpacing: true,
      start: () => this.startExpression(),
      end: () => `+=200%`,
      onUpdate: ({ progress }) => {
        if (!this.allImagesLoaded) return;
        const curFrame = Math.floor(this.frameCount * progress);
        this.updateImage(curFrame);
      },
    });
  }

  resizeHandler() {
    this.setSize();
    this.setCanvasSize();
    // this.scrollTrigger && this.scrollTrigger.kill();
    // this.setupScrollTrigger();
  }

  destroy() {
    this.timeout && clearTimeout(this.timeout);
    this.scrollTrigger && this.scrollTrigger.kill();
    // window.removeEventListener("resize", this.resizeHandler);
  }
}

gsap.registerPlugin(ScrollTrigger);

export default {
  props: {
    data: {
      type: Object,
      default: () => {},
    },
  },
  data: () => ({
    serviceImg1: require("~/static/images/about/service1.png"),
    framesAnimation: null,
  }),
  mounted() {
    this.framesAnimation = new FrameAnimation(
      this.$refs.canvas,
      this.$refs.canvasWrapper,
      this.$refs.container
    );

    this.$nextTick(() => {
      const { text1, text2 } = this.$refs;
      const text = [text1, text2];

      animateJS("splitTitleRight", this.$refs.title);
      animateJS("splitText", text);
      animateJS("fadeImage", this.$refs.image);
      animateJS("fadeUp", this.$refs.text3);
      animateJS("drawSvg", this.$refs.aboutLine1);
      animateJS("drawSvg", this.$refs.aboutLine2);
      animateJS("drawSvg", this.$refs.aboutLine3);
      animateJS("drawSvg", this.$refs.aboutLine4);
      animateJS("drawSvg", this.$refs.aboutLine5);

      this.animateCard();
    });
  },
  beforeDestroy() {
    this.framesAnimation.destroy();
  },
  methods: {
    addControls() {
      const camera = this.scene.getCamera();
      const model = this.scene.getModel();
      this.$datgui
        .add(camera.position, "y")
        .min(10)
        .max(200)
        .step(2)
        .name("y camera position");
      this.$datgui
        .add(camera.position, "z")
        .min(10)
        .max(200)
        .step(2)
        .name("z camera position");

      this.$datgui
        .add(model.rotation, "y")
        .min(-6.28)
        .max(6.28)
        .step(0.2)
        .name("rotate scene");

      const light = this.scene.getLight();
      this.$datgui
        .add(light, "intensity")
        .min(1)
        .max(5)
        .step(0.2)
        .name("light intensity");

      // rotate scene
    },
    animateCard() {
      const cards = this.$refs.card;

      cards.forEach((item) => {
        let counter = { value: 0 };
        let newValue = item.querySelector(
          ".about-service__info-number"
        ).textContent;

        gsap
          .timeline({
            scrollTrigger: {
              trigger: item,
            },
          })
          .from(
            item.querySelector(".about-service__info-border--top"),
            {
              duration: 2,
              scaleX: 0,
              transformOrigin: "left 50%",
              ease: "Power2.easeOut",
            },
            "together"
          )
          .from(
            item.querySelector(".about-service__info-border--bottom"),
            {
              duration: 2,
              scaleX: 0,
              transformOrigin: "left 50%",
              ease: "Power2.easeOut",
            },
            "together"
          )
          .from(
            item.querySelectorAll(".about-service__info-text p"),
            {
              duration: 1,
              x: 100,
              opacity: 0,
              ease: "Power4.easeInOut",
            },
            "together"
          )
          .to(
            counter,
            3,
            {
              value: newValue,
              roundProps: "value",
              onUpdate: function () {
                item.querySelector(".about-service__info-number").textContent =
                  counter.value;
              },
            },
            "together"
          );
      });
    },
  },
};
</script>

