  },
  data: () => ({
    serviceImg1: require("~/static/images/main/service1.jpg"),
  }),
  methods: {
    drawImage() {
      const tl = gsap.timeline({
        scrollTrigger: {
          trigger: ".main-service__img-logo",
          start: 'bottom bottom',
        },
      });

      tl.from(
        ".main-service__img-logo path",
        { delay: 0.5, duration: 2.5, drawSVG: '50% 50%' },
      )
    },
  },
  mounted() {
    this.drawImage();

    this.$nextTick(() => {
      const images = [this.$refs.image1, this.$refs.image2];
      const decor = [this.$refs.serviceDecor1, this.$refs.serviceDecor2, this.$refs.serviceDecor3];

      if (this.$refs.smallImage) {
        parallaxImage(this.$refs.smallImage, this.$refs.smallImageWrapper, 50);
      }

      if (images) {
        animateJS('fadeImage', images);
      }
      
      if (decor) {
        animateJS('drawSvg', decor);
      }

      if (this.$refs.title) {
        animateJS('splitTitleRight', this.$refs.title);
      }

      if (this.$refs.subTitle) {
        animateJS('splitText', this.$refs.subTitle);
      }

      if (this.$refs.textTop) {
        animateJS('splitText', this.$refs.textTop);
      }

      if (this.$refs.textBottom) {
        animateJS('splitTitleLeft', this.$refs.textBottom);
      }
    });
  }
};
</script>

