      type: String,
      default: "",
    },
  },
  methods: {
    mobileHeight() {
      let vh = window.innerHeight * 0.01;
      document.documentElement.style.setProperty('--vh', `${vh}px`);
    },
    moveItems() {
      const centerImageOffset = window.innerWidth >= 1024 || window.innerWidth < 768 ? 0 : '-50';
      gsap.timeline({
        scrollTrigger: {
          trigger: this.$refs.container,
          start: "top top",
          // pin: this.$refs.container,
          scrub: true,
        }
      })
      .to(this.$refs.imageCenter, {
        // bottom: "-17%",
        bottom: "0%",
        // scale: 1.25,
        scale: 1.5,
        yPercent: centerImageOffset,
        ease: "Power2.easeOut",
      }, "together")
      .to(this.$refs.imageLeft, {
        // top: "12.8%",
        top: "22.8%",
        left: "4.4%",
        ease: "Power2.easeOut" 
      }, "together")
      .to(this.$refs.imageRight, { 
        // top: "6.6%",
        top: "16.6%",
        right: "16.8%",
        ease: "Power2.easeOut" 
      }, "together")
      .to(this.$refs.title, { 
        y: "30vh",
        ease: "Power2.easeOut" 
      }, "together")
      .to(this.$refs.textBottom, { 
        y: "30vh",
        ease: "Power2.easeOut" 
      }, "together");
    }
  },
  mounted() {
    this.mobileHeight();
    this.moveItems();

    this.$nextTick(() => {
      const images = [
        this.$refs.imageCenter.querySelector("img"),
        this.$refs.imageLeft.querySelector("img"),
        this.$refs.imageRight.querySelector("img")
      ];
      animateJS('scaleFullImage', this.$refs.imageBg);
      animateJS('visibleItem', images);
      animateJS('splitTitleLeft', this.$refs.title);
      animateJS('splitText', this.$refs.textTop);
      animateJS('splitText', this.$refs.textBottom);
      animateJS('drawSvg', this.$refs.placeHeaderLine1);
      animateJS('drawSvg', this.$refs.placeHeaderLine2);
      animateJS('drawSvg', this.$refs.placeHeaderLine3);
    });
  }
};
</script>

